import React, { useState } from 'react';
import PostBook from './PostBook';


// Insert a book
async function insertBook(body) {
    try {
        const response = await fetch("api/books/", {
            'method': 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(body)
        });
        return await response.json();
    } catch (error) {
        return console.log(error);
    }
}


// Form
const BookForm = (props) => {
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [description, setDescription] = useState("")

    const postBook = () =>{
        PostBook.PostBook({title, author, description})
        .then((response) => props.newBook(response))
        .catch(error => console.log('error',error))
      }

    const handleSubmit=(event)=>{ 
        event.preventDefault()
        const book = { title, author, description }

        postBook()
    }

  return (

        <form onSubmit = {handleSubmit} >
            <label htmlFor="title">Title:</label>
            <input
                type="text" 
                id="title" 
                value={title} 
                onChange={(e)=>setTitle(e.target.value)}
            />

            <label htmlFor="author">Author:</label>
            <input
                type="text"
                id="author"
                value={author}
                onChange={(e)=>setAuthor(e.target.value)}
            />

            <label htmlFor="description">Description:</label>
            <textarea
                id="description"
                value={description}
                onChange={(e)=>setDescription(e.target.value)}
            />

            <button type="submit" value="Submit">Save New</button>
            <button type="submit" disabled={true}>Save</button>
            <button type="submit" disabled={true}>Delete</button>
        </form>

          /* 
          <label htmlFor="title" className="form-label">Title</label>
          <input 
          type="text"
          className="form-control" 
          placeholder ="Enter title"
          value={title}
          onChange={(e)=>setTitle(e.target.value)}
          required
          />

          <label htmlFor="body" className="form-label">Body</label>
          <textarea 
          className="form-control" 
          placeholder ="Enter body" 
          rows='6'
          value={body}
          onChange={(e)=>setBody(e.target.value)}
          required
          >
          </textarea>

          <button 
          className="btn btn-primary mt-2"
          >
          Publish article</button>
           */     
  )}

export default BookForm;
