class App extends React.Component {

    state = {
        users: [
        { name: "John" },
        { name: "Sarah" },
        { name: "Siri" },
        { name: "Jim" },
        { name: "Simon" },
      ],
      activeIndex: 0,
    }
    
    render() {
        const { users, activeIndex } = this.state;
    
        return (
          <div>
            {users.map((user, index) => (
              <div
                className={i === activeIndex ? 'active' : ''}
                onClick={() => this.setState({ activeIndex: i })}
                key={user.name}
              >
                {user.name}
              </div>
            ))}
          </div>
        )
      }
    }
