const BookList = (props) => {

    return (
        <div>
        {/* Display the book details if the book is not None */} 
   	    {props.books && props.books.map(book =>{
            return (
              <div key={book.book_id}>
                <h2> { book.title} </h2>
                <p> { book.author.italics() } </p>
                <p> { book.description } </p>
    	        <hr/>
              </div>
            )
            
            })}
        </div>
    )
}

export default BookList;
