const state = {
    users: [
    { name: "John" },
    { name: "Sarah" },
    { name: "Siri" },
    { name: "Jim" },
    { name: "Simon" },
    ],
    activeIndex: 0,
}
const MemeList = (props) => {
    

    const { users, activeIndex } = this.state;

    return (
        <div>
            {users.map((user, index) => (
                <div
                className={index === activeIndex ? 'active' : ''}
                onClick={() => this.setState({ activeIndex: index })}
                key={user.name}
                >
                {user.name}
                </div>
            ))}
        </div>
    )
}

export default MemeList;
