import React from 'react';

export default class App extends React.Component {

    state = {
        users: [
        { name: "John" },
        { name: "Sarah" },
        { name: "Siri" },
        { name: "Jim" },
        { name: "Simon" },
      ],
      activeIndex: null,
    }
    
    render() {
        const { users, activeIndex } = this.state;
    
        return (
          <div>
            {users.map((user, index) => (
              <div
                className={index === activeIndex ? 'active' : ''}
                onClick={() => this.setState({ activeIndex: index })}
                key={user.name}
              >
                {user.name}
              </div>
            ))}
          </div>
        )
      }
    }
