import React, { useState } from 'react';

const BookList = (props) => {
  const [ selectedBook, setSelectedBook ] = useState(null)
  
  const sendSelection = (book_id) => {
    props.getSelectedBook(book_id)
  }
  
  return (
    <div className="bookList">
    {props.books.isnot && props.books.map(book =>{
      return (
        <div 
          className={book.book_id === selectedBook ? 'active bookItem' : 'bookItem'}
          onClick={() => {setSelectedBook(book.book_id); sendSelection(book.book_id);}}
          key={ book.book_id }
        >
          <h2 className="bookTitle"> { book.title} </h2>
          <p className="bookAuthor"> { book.author } </p>
          <p className="bookDescription"> { book.description } </p>
        </div>
      )
      })}
    </div>
  )
}

export default BookList;
