import React, { useState, useEffect } from 'react';

const BookForm = (props) => {
    const [title, setTitle] = useState("")
    const [author, setAuthor] = useState("")
    const [description, setDescription] = useState("")
    const [errorMessage, setErrorMessage] = useState("")
    const [selectedBook, setSelectedBook] = useState(null)

    useEffect(() => {
        if (props.selectedBook && props.selectedBook != selectedBook) {
            setSelectedBook(props.selectedBook)
            setTitle(props.selectedBook.title)
            setAuthor(props.selectedBook.author)
            setDescription(props.selectedBook.description)
        }
    });

    const clearForm = () => {
        setTitle("")
        setAuthor("")
        setDescription("")
    }

    const validateForm = () => {
        console.log(title)
        if (title != "" && author != "" && description != null) {
            return true
        } else {
            setErrorMessage("Error: Missing fields")
            return false
        }
    }

    const saveNewBook = (event) => { 
        event.preventDefault()
        if (validateForm()) {
            const book = { title, author, description }
            fetch("api/books/", {
                method: "POST",
                headers: { "Content-Type": "application/json"},
                body: JSON.stringify(book)
            })
            .then((response) => props.updateBookList(response))
            clearForm()
        }
    }

    const deleteBook = (event) => {
        event.preventDefault()

        fetch("api/books/" + props.selectedBook.book_id + "/", {
            method: "DELETE",
            headers: { "Content-Type": "application/json"},
        })
        .then((response) => props.updateBookList(response))

        clearForm()
    }

  return (
        <form>
            <label htmlFor="title">Title:</label>
            <input
                type="text" 
                id="title" 
                value={title} 
                onChange={(e)=>setTitle(e.target.value)}
            />

            <label htmlFor="author">Author:</label>
            <input
                type="text"
                id="author"
                value={author}
                onChange={(e)=>setAuthor(e.target.value)}
            />

            <label htmlFor="description">Description:</label>
            <textarea
                id="description"
                value={description}
                onChange={(e)=>setDescription(e.target.value)}
            />

            <button onClick={(e) => saveNewBook(e)}>Save New</button>
            <button type="submit" disabled={true}>Save</button>
            <button onClick={(e) => deleteBook(e)} disabled={!props.selectedBook}>Delete</button>
            <p className="errorMessage">{errorMessage}</p>
        </form>
    )
}

export default BookForm;
