import React, { useState, useEffect } from 'react';
import BookList from './Components/BookList';
import BookForm from './Components/BookForm';
import './App.css';

function App() {
  const [databaseBooks, setBooks] = useState([]);

  useEffect(() => {
    fetch("/api/books/").then(response => response.json()).then(data => {
        setBooks(data.items)
    })
    .catch(error => console.log(error))
  }, []);

  const newBook = (book) => {
    const new_book = [...databaseBooks, book]
    console.log(book)
    useEffect(() => {
      fetch("/api/books/").then(response => response.json()).then(data => {
          setBooks(data.items)
      })
      .catch(error => console.log(error))
    }, []);
  }

  return (
    <div className="App">
      <div className="App-header">Library</div>
      <section>
        <div className="row">

          <div className="column">
            <BookForm newBook={newBook} />
          </div>

          <div className="column">
            <BookList books={databaseBooks} />
          </div>

        </div>
      </section>
    </div>
  );
}

export default App;
