import React, { useState } from 'react';

const BookList = (props) => {
  
  const sendSelection = (book) => {
    props.callGetSelectedBook(book)
  }
  
  if (props.books.length > 0) {
    return (
      <div className="bookList">
      {props.books && props.books.map(book =>{
        return (
          <div 
            className={book.book_id === props.selectedBook ? 'active bookItem' : 'bookItem'}
            onClick={() => {setSelectedBook(book.book_id); sendSelection(book);}}
            key={ book.book_id }
          >
            <h2 className="bookTitle"> { book.title} </h2>
            <p className="bookAuthor"> { book.author } </p>
          </div>
        )
        })}
      </div>
    )
  } else {
    return null
  }
}

export default BookList;
