import React, { useState } from 'react';


// Insert a book
async function insertBook(body) {
    try {
        const response = await fetch("/books/", {
            'method': 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(body)
        });
        return await response.json();
    } catch (error) {
        return console.log(error);
    }
}


// Form
const BookForm = (props) => {
    const [title, setTitle] = useState('')
    const [author, setAuthor] = useState('')
    const [body, setBody] = useState('')

    insertBook({title, body})

    const handleSubmit=(event)=>{ 
      event.preventDefault()
      insertBook()
      setTitle('')
      setBody('')
    }

  return (
    <div className="shadow p-4">

        <form onSubmit = {handleSubmit} >
              <label for="title">Title:</label>
              <input type="text" id="title" value={title}></input>
              <label for="author">Author:</label>
              <input type="text" id="author" value={author}></input>
              <label for="description">Description:</label>
              <textarea id="description"></textarea>
              <button type="submit" form="form1" value="Submit">Save New</button>
              <button type="submit" form="form1" value="Submit">Save</button>
              <button type="submit" form="form1" value="Submit">Delete</button>
        </form>


          {/* 
          <label htmlFor="title" className="form-label">Title</label>
          <input 
          type="text"
          className="form-control" 
          placeholder ="Enter title"
          value={title}
          onChange={(e)=>setTitle(e.target.value)}
          required
          />

          <label htmlFor="body" className="form-label">Body</label>
          <textarea 
          className="form-control" 
          placeholder ="Enter body" 
          rows='6'
          value={body}
          onChange={(e)=>setBody(e.target.value)}
          required
          >
          </textarea>

          <button 
          className="btn btn-primary mt-2"
          >
          Publish article</button>
           */}
              

    </div>
  )}

export default BookForm;
