import React, { useState, useEffect } from 'react';
import BookList from './Components/BookList';
import BookForm from './Components/BookForm';
import './App.css';

function App() {
  const [databaseBooks, setBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    fetch("/api/books/").then(response => response.json()).then(data => {
        setBooks(data.items)
    })
  }, []);

  const updateBookList = () => {
    fetch("/api/books/").then(response => response.json()).then(data => {
      setBooks(data.items)
    })
  }

  const bookDeleted = () => {
    setSelectedBook(null)
    updateBookList()
  }

  const getSelectedBook = (selectedBook) => {
    setSelectedBook(selectedBook)
  }

  return (
    <div className="App">
      <div className="App-header">Library</div>
      <section>
        <div className="row">

          <div className="column">
            <BookForm 
              updateBookList={updateBookList}
            selectedBook={selectedBook} />
          </div>

          <div className="column">
            <BookList books={databaseBooks} getSelectedBook={getSelectedBook}/>
          </div>

        </div>
      </section>
    </div>
  );
}

export default App;
