import React, { useState, useEffect } from 'react';
import BookList from './Components/BookList';
import './App.css';

function App() {
  const [databaseBooks, setBooks] = useState([]);

  useEffect(() => {
    fetch("/api/books/").then(response => response.json()).then(data => {
        setBooks(data.items)
    })
.then(response => response.json())
.catch(error => console.log(error))
  }, []);

  return (
    <div className="App">
      <div className="App-header">Library</div>
      <section>
        <div className="row">

          <div className="column">
            <form>
              <label for="fname">Title:</label>
              <input type="text" id="fname" name="fname"></input>
              <label for="author">Author:</label>
              <input type="text" id="fname" name="fname"></input>
              <label for="description">Description:</label>
              <textarea id="description"></textarea>
              <button type="submit" form="form1" value="Submit">Save New</button>
              <button type="submit" form="form1" value="Submit">Save</button>
              <button type="submit" form="form1" value="Submit">Delete</button>
            </form>
          </div>

          <div className="column">
            <BookList books={databaseBooks} />
          </div>

        </div>
      </section>
    </div>
  );
}

export default App;
