import React, { useState, useEffect } from 'react';
import BookList from './Components/BookList';
import BookForm from './Components/BookForm';
import './App.css';

function App() {
  const [databaseBooks, setBooks] = useState([]);
  const [selectedBook, _setSelectedBook] = useState(null);

  useEffect(() => {
    updateBookList()
  }, []);

  const updateBookList = () => {
    fetch("/api/books/")
      .then(response => response.json())
      .then(data => { setBooks(data.items) })
  }

  const bookAddedOrDeleted = () => {
    updateBookList()
    setSelectedBook(null)
  }

  const setSelectedBook = (book) => {
    updateBookList()
    _setSelectedBook(book)
  }

  const changeSelectedBook = (selectedBook) => {
    setSelectedBook(selectedBook)
  }

  return (
    <div className="App">
      <div className="App-header">Library</div>
      <section>
        <div className="row">

          <div className="column">
            <BookForm 
              callUpdateBookList={updateBookList}
              callBookDeleted={bookAddedOrDeleted}
              selectedBook={selectedBook}
            />
          </div>

          <div className="column">
            <BookList
              callChangeSelectedBook={changeSelectedBook}
              selectedBook={selectedBook}
              books={databaseBooks}
            />
          </div>

        </div>
      </section>
    </div>
  );
}

export default App;
